import React from 'react'
import styled from 'styled-components'

const UserCard = (props) => {
  return (
    <UserCardLayout>
        <div className='name'>{
            `${props.data.name}(${props.data.gender === "male" ? "남" : "여"})` // 이름과 성별이 api에서 서로 다른 값으로 넘어오기 때문에 백틱 이용해서 이렇게 받아옴
        }</div>
        <div className='part'>{props.data.part}</div>
        <div className='major'>{props.data.major}</div>
    </UserCardLayout>
  )
}

export default UserCard

const UserCardLayout = styled.div`
    display: flex;
    flex-direction: column;
    justify-content: center;
    height: 15rem;
    background-color: rgba(255,179,122,1);
    border-radius: 1rem;
    padding-left: 2rem;
    // .name은 클래스이름 사용한 것.
    & > .name{
        font-size: 2.5rem;
        margin-bottom: 1.5rem;
    }
    & > .part{
        font-size: 1.8rem;
        margin-bottom: 0.6rem;
    }
    & > .major{
        font-size: 1.3rem;
    }
`
